import React from 'react';

const produtos = [
  {
    id: 1,
    nome: 'Smartphone',
    preco: 'R$ 2000',
    cores: ['#29d8d5', '#252a34', '#fc3766'],
  },
  {
    id: 2,
    nome: 'Notebook',
    preco: 'R$ 3000',
    cores: ['#ffd045', '#d4394b', '#f37c59'],
  },
  {
    id: 3,
    nome: 'Tablet',
    preco: 'R$ 1500',
    cores: ['#365069', '#47c1c8', '#f95786'],
  },
];

const App = () => {
  return (
    //getting the array livros that contains objects
    //unpacking "livros" by year - line 16
    //filtering by year greater than or equal to 1998
    //unpacking "livros" by name and year - line 17
    <section>
      {produtos
        .filter(({ preco }) => Number(preco.slice(3)) > 1500)
        .map(({ id, nome, preco, cores }) => (
          <div key={id}>
            <h1>{nome}</h1>
            <p>Pre√ßo: {preco}</p>
            <p style={{ color: 'red' }}>{cores[0]}</p>
            <p>{cores[1]}</p>
            <p>{cores[2]}</p>
          </div>
        ))}
    </section>
  );
};

export default App;
