    // id: "Tablet"
    // nome: "Tablet"
    // preco: "1899"
    // usuario_id: "lobo@origamid.com"
    // vendido: "false"
  }

  return (
    <div>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        Notebook
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        Smartphone
      </button>
      <button style={{ margin: '.5rem' }} onClick={handleClick}>
        Tablet
      </button>

      <Produto />
    </div>
  );
};
export default App;
